import React, { useState } from 'react';

const App: React.FC = () => {
  const [tiles, setTiles] = useState<number[]>([1, 2, 3, 4, 5, 6, 7, 8, 0]);
  const [moveCount, setMoveCount] = useState<number>(0);
  const shuffleTiles = () => {
    const mixed = mixTiles();
    setTiles(mixed);
    setMoveCount(0);
  };

  const swapTiles = (tile1: number, tile2: number) => {
    const newTiles = [...tiles];
    [newTiles[tile1], newTiles[tile2]] = [newTiles[tile2], newTiles[tile1]];
    setTiles(newTiles);
  };

  const handleTileClick = (index: number) => {
    const tileValue = tiles[index];
    moveCount++;
    if (tileValue === 0) { return; }
    console.log(`Clicked tile at index ${index}, value: ${tileValue}`);
    if (tiles[index - 3] === 0 && index - 3 >= 0) {
      // Move up
      swapTiles(index, index - 3);
    } else if (tiles[index + 3] === 0 && index + 3 < tiles.length) {
      // Move down
      swapTiles(index, index + 3);
    } else if (tiles[index - 1] === 0 && index % 3 !== 0) {
      // Move left (check that the tile is not at the left edge)
      swapTiles(index, index - 1);
    } else if (tiles[index + 1] === 0 && (index + 1) % 3 !== 0) {
      // Move right (check that the tile is not at the right edge)
      swapTiles(index, index + 1);
    }
  };

  function isSolvable(tiles: number[]): boolean {
    let inversions = 0;
    // Count inversions (ignoring 0)
    for (let i = 0; i < tiles.length; i++) {
      for (let j = i + 1; j < tiles.length; j++) {
        if (tiles[i] > 0 && tiles[j] > 0 && tiles[i] > tiles[j]) {
          inversions++;
        }
      }
    }

    // For a 3×3 puzzle, solvability depends on inversions being even
    return inversions % 2 === 0;
  }

  function mixTiles(): number[] {
    const tiles = [1, 2, 3, 4, 5, 6, 7, 8, 0];

    // Fisher-Yates Shuffle
    for (let i = tiles.length - 1; i > 0; i--) {
      const randomIndex = Math.floor(Math.random() * (i + 1));
      [tiles[i], tiles[randomIndex]] = [tiles[randomIndex], tiles[i]];
    }

    // Check solvability for 3x3 puzzle
    if (!isSolvable(tiles)) {
      [tiles[0], tiles[1]] = [tiles[1], tiles[0]];
    }

    return tiles;
  }



  return (
    <div className="App">
      <h1>3×3 Sliding Puzzle</h1>
      <button className="btn" onClick={shuffleTiles}>Shuffle</button>
      <div className="puzzle-grid">
        {tiles.map((value, idx) => {
          const isEmpty = value === 0; // 0 is empty
          const className = `
            puzzle-cell 
            ${isEmpty ? 'empty' : ''} 
            ${idx === 0 ? 'top-left' : ''} 
            ${idx === 2 ? 'top-right' : ''} 
            ${idx === 6 ? 'bottom-left' : ''} 
            ${idx === 8 ? 'bottom-right' : ''}
          `.trim();

          return (
            <div
              key={idx}
              className={className}
              onClick={() => handleTileClick(idx)}
            >
              {isEmpty ? '' : value}
            </div>
          );
        })}
      </div>\
      <div className='count'>{moveCount}</div>
    </div>
  );
};

export default App;
